<?php// Get the REST call path from the AJAX application// Is it a POST or a GET?$email = $_GET['yt_email'];$pass = $_GET['yt_pass'];$service = $_GET['yt_service'];$appname = $_GET['yt_appname'];// Construct an HTTP POST request$clientlogin_url = "https://www.google.com/accounts/ClientLogin";$clientlogin_post = array(    //"accountType" => "GOOGLE",    "Email" => $email,    "Passwd" => $pass,    "service" => $service,    "source" => $appname);// Initialize the curl object$curl = curl_init($clientlogin_url);// Set some options (some for SHTTP)curl_setopt($curl, CURLOPT_POST, true);curl_setopt($curl, CURLOPT_POSTFIELDS, $clientlogin_post);curl_setopt($curl, CURLOPT_HTTPAUTH, CURLAUTH_ANY);curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, false);curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);curl_setopt($curl, CURLOPT_FOLLOWLOCATION, true);// Execute$response = curl_exec($curl);// Get the Auth string and save itpreg_match("/Auth=([a-z0-9_\-]+)/i", $response, $matches);$auth = $matches[1];echo "The auth string is: " . $auth;